import React from 'react';
import api from "../server_api/api";
import {Link} from "react-router-dom";

class Game extends React.Component{
    constructor(props) {
        super(props);
        this.state = {

            user_name: this.props.name,
            min_number: parseInt(this.props.min_number),
            max_number: parseInt(this.props.max_number),
            computer_guess: [],
            user_number: null,
            one_comp_guess: this.getRndInteger(parseInt(this.props.min_number), parseInt(this.props.max_number)),


        };

        // console.log(this.state)
    }



    getRndInteger(min, max) {
        return  Math.floor(Math.random() * (max - min + 1) ) + min;

    };

    smaller_number = () => {
        let max = this.state.one_comp_guess;
        this.state.computer_guess.push(max);
        this.setState({
             one_comp_guess: this.getRndInteger(this.state.min_number, max )
        })
    };

    biger_number = () => {
        let min = this.state.one_comp_guess;
        this.state.computer_guess.push(min);
        this.setState({
            one_comp_guess: this.getRndInteger(min,this.state.max_number )
        })
    };

    async send_data() {
        this.setState({
            user_number: this.state.one_comp_guess,
        });
        const data = await api.addGame(this.state);
        console.log(data);
    };


    render() {
        console.log(this.state);
        return(
            <div style={{margin: "20px", padding: "20px"}}>

                <div className="alert alert-primary" role="alert">
                    {this.state.one_comp_guess}
                </div>

                <div>
                    <button type="button" className="btn btn-outline-primary" onClick={this.smaller_number}>My number is smaller than that number</button>
                </div>


                <div>
                    <button type="button" className="btn btn-outline-success" onClick={this.send_data}>it's my number</button>
                </div>

                <div>
                    <button type="button" className="btn btn-outline-primary" onClick={this.biger_number}>My number is greater than that number</button>
                </div>


            </div>
        )
    }
}

export default Game;
