                    <button type="button" className="btn btn-outline-success" onClick={this.send_data()}>it's my number</button>
                </div>

                <div>
                    <button type="button" className="btn btn-outline-primary" onClick={this.biger_number()}>My number is greater than that number</button>
                </div>


            </div>
        )
    }
}

export default Game;
