    constructor(props) {
        super(props);
        this.state = {
            data: {
                one_comp_guess: null,
                user_name: this.props.name,
                min_number: this.props.min_number,
                max_number: this.props.max_number,
                computer_guess: [],
                user_number: null,

            }

        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }



    async componentDidMount() {
        this.setState({
            one_comp_guess: this.getRndInteger(this.state.min_number, this.state.max_number)
        })

        // const data = await api.addGame(this.state.data);
    }

    getRndInteger(min, max) {
        return Math.floor(Math.random() * (max - min + 1) ) + min;
    }



