                <div>
                    <button type="button" className="btn btn-outline-primary">My number is smaller than that number</button>
                </div>


                <div>
                    <button type="button" className="btn btn-outline-success">it's my number</button>
                </div>

                <div>
                    <button type="button" className="btn btn-outline-primary">My number is greater than that number</button>
                </div>


            </div>
        )
    }
}

export default Game;
