import React from 'react';
import validate, {field} from './validator';
import InputErrors from './inputErrors';

class SignUp extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            firstName: field({name: 'firstName', isRequired:true, minLength: 2}),
            lastName: field({name: 'lastName',isRequired: true, minLength: 2}),
            email: field({name: 'email', required: true, pattern: /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/}),
            password: field({name: 'password', isRequired:true, minLength: 6}),
            phone: field({name: 'phone', isRequired:true}),

        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange({target:{name,value}}){
        const errors = validate(name, value, this.state[name].validations);

        this.setState({
            [name]: {
                ...this.state[name],
                value,
                errors
            }
        });
    }

    handleSubmit = (event)=> {
        event.preventDefault();
        let isOK = true;
        for(let prop in this.state){
            const field = this.state[prop];
            const errors = validate(prop, field.value, field.validations);
            if(errors.length){
                isOK = false;
                this.setState({
                    [prop]: {
                        ...this.state[prop],
                        errors
                    }
                });
            }
        }

        if(isOK){
            const result = {};
            for(let prop in this.state){
                result[prop] = this.state[prop].value;
            }
            console.log(result);
        }
    };

    render() {
        return (
            <div class="container" onClick={ this.props.handleChildClick()}>
                <div class="row">
                    <div class="col-md-5 mx-auto">
                        <div class="myform form ">
                            <div class="logo mb-3">
                                <div class="col-md-12 text-center">
                                    <h1 >Sign up</h1>
                                </div>
                            </div>
                            <form action="#" name="registration" onSubmit={this.handleSubmit}>
                                <div class="form-group">
                                    <label for="exampleInputEmail1">First Name</label>
                                    <input type="text"  name="firstName"
                                           className="form-control" id="firstname" aria-describedby="emailHelp"
                                           placeholder="Enter first name" onBlur={this.handleChange}/>
                                    <InputErrors errors={this.state.firstName.errors}></InputErrors>
                                </div>
                                <div class="form-group">
                                    <label for="exampleInputEmail1">Last Name</label>
                                    <input type="text"  name="lastName"
                                           className="form-control" id="lastname" aria-describedby="emailHelp"
                                           placeholder="Enter last name" onBlur={this.handleChange} />
                                    {this.state.lastName.errors.map( error =>(
                                        <small className={"form-text text-danger"}>{error}</small>
                                        ))
                                    }
                                </div>
                                <div class="form-group">
                                    <label for="exampleInputEmail1">Email address</label>
                                    <input type="email" name="email"
                                           className="form-control" id="email" aria-describedby="emailHelp"
                                           placeholder="Enter email" onBlur={this.handleChange} />
                                    {this.state.email.errors.map( error =>(
                                        <small className={"form-text text-danger"}>{error}</small>
                                        ))
                                    }
                                </div>
                                <div class="form-group">
                                    <label for="exampleInputEmail1">Password</label>
                                    <input type="password" min={6} name="password" id="password"
                                           className="form-control" aria-describedby="emailHelp"
                                           placeholder="Enter password" onBlur={this.handleChange} />
                                    {this.state.password.errors.map( error =>(
                                        <small className={"form-text text-danger"}>{error}</small>
                                    ))
                                    }
                                </div>

                                <div className="form-group">
                                    <label htmlFor="exampleInputEmail1">Phone</label>
                                    <input type="text" name="phone" id="phone"
                                           className="form-control" aria-describedby="phoneHelp"
                                           placeholder="Enter phone" onBlur={this.handleChange} />
                                    {this.state.phone.errors.map( error =>(
                                        <small className={"form-text text-danger"}>{error}</small>
                                    ))
                                    }
                                </div>

                                <div class="col-md-12 text-center mb-3">
                                    <button type="submit" class=" btn btn-block mybtn tx-tfm">Get Started For Free</button>
                                </div>
                                <div class="col-md-12 ">
                                    <div class="form-group">
                                        <p class="text-center"><button className={"form_link"}  onClick={this.props.activateSignupAndLogin()}>Already have an account?</button></p>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default SignUp;
